package test;

public class VariablesTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i = 10;
		System.out.println("i");
		System.out.println(i);
		i = 20;
		System.out.println(i);
		
		i = i+10;
		System.out.println(i);
	
		i=i*2;
		System.out.println(i);
		
		System.out.println(i++);
		System.out.println(i);
		
		System.out.println(++i);
		
		char a ='b';
		System.out.println(a);
		
		double c= 10.5;
		System.out.println(c);
		
	}

}
